public class Answer {
private int qid;
private String options;
private int oid;


public Answer(String options) {
	super();
	this.options = options;
}
public Answer() {
	super();
	// TODO Auto-generated constructor stub
}
public int getQid() {
	return qid;
}
public void setQid(int qid) {
	this.qid = qid;
}
public String getOptions() {
	return options;
}
public void setOptions(String options) {
	this.options = options;
}
public int getOid() {
	return oid;
}
public void setOid(int oid) {
	this.oid = oid;
}


	
	
}
